import React from "react";
import Articles from "./components/articles";
import Filters from "./components/filters";
import { useRouter } from "next/router";
import { getProjects, getCategories } from "../lib/api";

const Projects = ({ categories, projects, catProj }) => {
  const router = useRouter();

  return (
    <div>
      <style jsx global>{`
        body {
          background-color: #151515;
          background-image: radial-gradient(
            circle at bottom right,
            #2c2c2c,
            #0c0c0c
          );
          background-attachment: fixed;
          color: #f8f8ff;
        }
      `}</style>
      <h1 className="d-none">Projects</h1>
      <Filters tags={categories} />
      <div className="container animate__animated animate__fadeIn">
        {!router.query.id ? (
          <Articles articles={projects} />
        ) : (
          <Articles articles={catProj} />
        )}
      </div>
    </div>
  );
};

export async function getStaticProps() {
  const projects = (await getProjects()) || [];
  const categories = (await getCategories()) || [];
  return {
    props: { projects, categories },
    unstable_revalidate: 1
  };
}

export default Projects;
