import React from "react";
import Articles from "../components/articles";
import Filters from "../components/filters";
import { useRouter } from "next/router";
import { getProjects, getCategories, getCategoryProjects } from "../lib/api";
import Layout from "../components/layout";

const Projects = ({ categories, projects, categoryProjects }) => {
  const router = useRouter();

  return (
    <Layout>
      <div>
        <style jsx global>{`
          body {
            background-color: #151515;
            background-image: radial-gradient(
              circle at bottom right,
              #2c2c2c,
              #0c0c0c
            );
            background-attachment: fixed;
            color: #f8f8ff;
          }
        `}</style>
        <h1 className="d-none">Projects</h1>
        <Filters tags={categories} />
        <div className="container animate__animated animate__fadeIn">
          {!router.query.id ? (
            <Articles articles={projects} />
          ) : (
            <Articles articles={categoryProjects} />
          )}
        </div>
      </div>
    </Layout>
  );
};

export async function getStaticPaths() {
  const categories = (await getCategories()) || [];
  return {
    paths: categories.map(category => ({
      params: {
        id: category.id
      }
    })),
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  const projects = (await getProjects()) || [];
  const categories = (await getCategories()) || [];
  const categoryProjects = (await getCategoryProjects(params.id)) || [];

  return {
    props: { projects, categories, categoryProjects },
    revalidate: 1
  };
}

export default Projects;
